function LangCard({
	tooltipText,
	children,
}: Readonly<{
	tooltipText: string;
	children: React.ReactNode;
}>) {
	return (
		<div className='py-4 px-4 md:px-16 border border-blue-600 rounded-lg w-fit relative group'>
			{children}

			<span className='absolute rounded bg-white p-1 text-base font-medium text-black opacity-0 group-hover:opacity-100 transition-opacity duration-300 top-0 right-0'>
				{tooltipText}
			</span>
		</div>
	);
}

